import arquant from './img/logo-b.svg';
import chatgptimg from './img/chatgpt.svg';
import logochat from './img/logochat.png';
import   { useState, useEffect } from 'react';
import './css/App.css';
import './css/normalize.css';


function App() {

  useEffect(()=> {
    getEngines();
  }, [])

  //Add state for input and chat log


  const [input, setInput] = useState("");
  const [models, setModels] = useState([]);
  const [chatLog, setChatLog] = useState([{
    user: "gpt",
    message: "Cómo puedo ayudarte hoy?"
  }, {
    user: "me",
    message: "i want to know something"
  }])

  //clear chat
  function clearChat() {
    setChatLog([]);
  }

  function getEngines() {
    fetch("http://localhost:3080/models")
      .then(res => res.json())
      .then(data => setModels(data.models.data));
  }

  async function handleSubmit(e) {
    e.preventDefault();
    let chatLogNew = [...chatLog, {user: "Yo", message: `${input}`}];
    setInput("");
    setChatLog(chatLogNew)

    //Fetch response to the api, combining the chat log array of messages 
    // and sending it as a message to localhost 3000 as a post
    const messages = chatLogNew.map((message) => message.message)
    .join("\n");

    const response = await fetch("http://localhost:3080/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        message: messages
      })
    });
    const data = await response.json();
    await setChatLog([...chatLogNew, {user: "gpt", message: `${data.message}`}])
    console.log(data.message);
  }
  return (
    <div className="App">
      <aside className="menu">
        <img alt="arqlogo" src={arquant}/>
        <div className='side-menu-button' onClick={clearChat}>
          <span>+</span>
          Nuevo Chat
        </div>
      </aside>
      <section className="chatbox">
        <div className='chat-log'>
          {chatLog.map((message, index) => (
            <ChatMessage key={index} message={message} />
          ))}
        </div>

        <div className='chat-input-holder'> 
        <form onSubmit={handleSubmit}>
          <input rows="1" className='chat-input-text-area' value={input} onChange={(e)=> setInput(e.target.value)}/>
        </form>
        </div>

      </section>
    </div>
  );
}

export default App;
const ChatMessage = ({message}) => {
  return (
    <div className='chat-message'>
      <div className='chat-message-center'>
          <div className={`avatar ${message.user == "gpt" && "chatgpt"}`}>{message.user=="gpt" && <img src={chatgptimg} alt="ai" />}</div>
          <div className='message'>{message.message}</div>
      </div>
    </div>   
  );

}

